serializedTelegrafConfig.value:
{{ $partitionLimit := 10 }}
{{ $sqlAzureConnections := .Parameters.sqlAzureConnections }}
{{ $sqlAzurePartitioned := chunk $partitionLimit $sqlAzureConnections }}
{{ $sqlVmConnections := .Parameters.sqlVmConnections }}
{{ $sqlVmPartitioned := chunk $partitionLimit $sqlVmConnections }}
{{ $sqlManagedInstanceConnections := .Parameters.sqlManagedInstanceConnections }}
{{ $sqlManagedInstancePartitioned := chunk $partitionLimit $sqlManagedInstanceConnections }}

{{- if $sqlAzureConnections}}
  {{- range $ind, $p := $sqlAzurePartitioned }}
  [[inputs.sqlserver]]
    interval = "50s"
    collection_jitter = "20s"
    servers = [
      {{- range $ind, $cs := $p }}
        {{- if $ind}}, {{end}}
		      "{{ $cs }}"
		  {{- end}}
	  ]
	  database_type = "AzureSQLDB"
	  include_query = ['AzureSQLDBOsWaitstats','AzureSQLDBMemoryClerks','AzureSQLDBDatabaseIO','AzureSQLDBServerProperties','AzureSQLDBPerformanceCounters','AzureSQLDBResourceStats','AzureSQLDBResourceGovernance']
  {{- end}}
{{- end}}

{{- if $sqlVmConnections}}
  {{- range $ind, $p := $sqlVmPartitioned }}
  [[inputs.sqlserver]]
    interval = "50s"
    collection_jitter = "20s"
    servers = [
      {{- range $ind, $cs := $p }}
        {{- if $ind}}, {{end}}
		      "{{ $cs }}"
		  {{- end}}
	  ]
	  database_type = "SQLServer"
	  include_query = ['SQLServerWaitStatsCategorized','SQLServerMemoryClerks','SQLServerDatabaseIO','SQLServerProperties','SQLServerPerformanceCounters','SQLServerVolumeSpace','SQLServerCpu']
  {{- end}}
{{- end}}

{{- if $sqlManagedInstanceConnections}}
  {{- range $ind, $p := $sqlManagedInstancePartitioned }}
  [[inputs.sqlserver]]
    interval = "50s"
    collection_jitter = "20s"
    servers = [
      {{- range $ind, $cs := $p }}
        {{- if $ind}}, {{end}}
		      "{{ $cs }}"
		  {{- end}}
	]
	database_type = "AzureSQLManagedInstance"
	include_query = ['AzureSQLMIOsWaitstats','AzureSQLMIMemoryClerks','AzureSQLMIDatabaseIO','AzureSQLMIServerProperties','AzureSQLMIPerformanceCounters','AzureSQLMIResourceStats','AzureSQLMIResourceGovernance']
  {{- end}}
{{- end}}

[[inputs.internal]]
